import{_ as n,c as p,G as e,j as s,a4 as l,B as o,o as c}from"./chunks/framework.BY-a5-Lk.js";const k=JSON.parse('{"title":"前言","description":"","frontmatter":{},"headers":[],"relativePath":"杂谈/02.md","filePath":"杂谈/02.md"}'),i={name:"杂谈/02.md"};function d(m,t,u,_,h,f){const a=o("Linkcard"),r=o("confetti");return c(),p("div",null,[e(a,{title:"Robomaster的M2006电机驱动教程",description:"https://blog.breadq.top/新的探索/Chapter_00/00.html",logo:"/logo_1.jpg"}),t[0]||(t[0]=s("hr",null,null,-1)),e(r),t[1]||(t[1]=l('<h1 id="前言" tabindex="-1">前言 <a class="header-anchor" href="#前言" aria-label="Permalink to &quot;前言&quot;">​</a></h1><blockquote><p>🍊Hello，各位好，我是面包！</p><p>这篇文章记录了一些我在写论文时对扩散模型的的一些理解。</p></blockquote><p>扩散过程:对原始图片进行噪声变换的过程，分为前向扩散和反向生成，前向扩散是图片不断添加高斯噪声的过程，后向生成是添加噪声之后的图片不断减去噪声的过程。</p><p>VAE:一种将图像数据进行压缩方式所依赖的模型，包括解码和编码。</p><p>CLIP有两种模态，分别呈现为文本编码器和视觉编码器，它们分别将文本和图像转化为嵌入向量对采样器进行特征修饰，使生成的图片不断趋近于期望的图片。</p><div class="tip custom-block"><p class="custom-block-title">模型</p><p>让机器通过500字的中心思想和梗概去扩写出一篇1万字的文章出来，和原文章肯定不是每字每句都相同的，但看过的人一对比发现和原文章竟然大体上雷同。这个过程便是人工智能的训练学习的过程。我们经常说的大模型训练就是这个意思，学习训练出来的规律总和，就叫“模型”。 <a href="https://zhuanlan.zhihu.com/p/666649803" target="_blank" rel="noreferrer">知乎</a></p></div>',6))])}const g=n(i,[["render",d]]);export{k as __pageData,g as default};
